import React, { useState, useEffect } from 'react';
import './App.css';

// API base URL
const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
  const [user, setUser] = useState(null);
  const [content, setContent] = useState([]);
  const [analytics, setAnalytics] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [showAdmin, setShowAdmin] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Check if user is logged in on app load
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      verifyToken(token);
    }
  }, []);

  // Verify JWT token
  const verifyToken = async (token) => {
    try {
      const response = await fetch(`${API_BASE}/auth/verify`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        localStorage.setItem('token', token);
        loadUserData();
      } else {
        localStorage.removeItem('token');
      }
    } catch (error) {
      console.error('Token verification failed:', error);
      localStorage.removeItem('token');
    }
  };

  // Load user data after login
  const loadUserData = async () => {
    try {
      const token = localStorage.getItem('token');
      const [contentRes, analyticsRes] = await Promise.all([
        fetch(`${API_BASE}/content/my-content`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }),
        fetch(`${API_BASE}/analytics/overview`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        })
      ]);

      if (contentRes.ok) {
        const contentData = await contentRes.json();
        setContent(contentData.content || []);
      }

      if (analyticsRes.ok) {
        const analyticsData = await analyticsRes.json();
        setAnalytics(analyticsData);
      }
    } catch (error) {
      console.error('Failed to load user data:', error);
    }
  };

  // Login function
  const handleLogin = async (userData) => {
    try {
      setLoading(true);
      setError('');
      
      const response = await fetch(`${API_BASE}/auth/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      const data = await response.json();

      if (response.ok) {
        setUser(data.user);
        localStorage.setItem('token', data.token);
        setShowLogin(false);
        loadUserData();
        
        // Show admin panel if user is admin
        if (data.user.role === 'admin') {
          setShowAdmin(true);
        }
      } else {
        setError(data.error || 'Login failed');
      }
    } catch (error) {
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Register function
  const handleRegister = async (userData) => {
    try {
      setLoading(true);
      setError('');
      
      const response = await fetch(`${API_BASE}/auth/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      const data = await response.json();

      if (response.ok) {
        setUser(data.user);
        localStorage.setItem('token', data.token);
        setShowRegister(false);
      } else {
        setError(data.error || 'Registration failed');
      }
    } catch (error) {
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Logout function
  const handleLogout = () => {
    setUser(null);
    setContent([]);
    setAnalytics(null);
    setShowAdmin(false);
    localStorage.removeItem('token');
  };

  // Upload content function
  const handleUploadContent = async (contentData) => {
    try {
      setLoading(true);
      setError('');
      
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE}/content/upload`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(contentData),
      });

      const data = await response.json();

      if (response.ok) {
        setContent(prev => [data.content, ...prev]);
        loadUserData(); // Refresh analytics
        return true;
      } else {
        setError(data.error || 'Upload failed');
        return false;
      }
    } catch (error) {
      setError('Network error. Please try again.');
      return false;
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>AutoPromote</h1>
        <nav>
          {user ? (
            <div className="user-nav">
              <span>Welcome, {user.name}!</span>
              {user.role === 'admin' && (
                <button onClick={() => setShowAdmin(!showAdmin)}>
                  {showAdmin ? 'User View' : 'Admin Panel'}
                </button>
              )}
              <button onClick={handleLogout}>Logout</button>
            </div>
          ) : (
            <div>
              <button onClick={() => { setShowLogin(true); setShowRegister(false); setError(''); }}>Login</button>
              <button onClick={() => { setShowRegister(true); setShowLogin(false); setError(''); }}>Register</button>
            </div>
          )}
        </nav>
      </header>

      <main>
        {error && <div className="error-message">{error}</div>}
        {loading && <div className="loading">Loading...</div>}

        {showLogin && <LoginForm onLogin={handleLogin} onClose={() => setShowLogin(false)} loading={loading} />}
        {showRegister && <RegisterForm onRegister={handleRegister} onClose={() => setShowRegister(false)} loading={loading} />}
        
        {user && showAdmin && (
          <AdminDashboard 
            user={user} 
            onClose={() => setShowAdmin(false)}
            API_BASE={API_BASE}
          />
        )}
        
        {user && !showAdmin && (
          <UserDashboard
            user={user}
            content={content}
            analytics={analytics}
            onUpload={handleUploadContent}
            onRefresh={loadUserData}
            API_BASE={API_BASE}
          />
        )}
        
        {!user && !showLogin && !showRegister && (
          <div className="welcome-section">
            <h2>Welcome to AutoPromote</h2>
            <p>AI-powered platform for automated content promotion and monetization</p>
            <div className="features">
              <div className="feature">
                <h3> Viral Promotion</h3>
                <p>Get over 2 million views per content piece</p>
              </div>
              <div className="feature">
                <h3> High Revenue</h3>
                <p>$900,000 revenue per million views</p>
              </div>
              <div className="feature">
                <h3> AI Powered</h3>
                <p>Automatic promotion across multiple platforms</p>
              </div>
            </div>
            <button onClick={() => setShowRegister(true)}>Get Started</button>
          </div>
        )}
      </main>
    </div>
  );
}

// Login Form Component
const LoginForm = ({ onLogin, onClose, loading }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin({ email, password });
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="close-btn" onClick={onClose}></button>
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email:</label>
            <input 
              type="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
              disabled={loading}
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input 
              type="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
              disabled={loading}
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>
      </div>
    </div>
  );
};

