// User Dashboard Component
const UserDashboard = ({ user, content, analytics, onUpload, onRefresh, API_BASE }) => {
  const [showUpload, setShowUpload] = useState(false);

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h2>Your Dashboard</h2>
        <div className="dashboard-actions">
          <button onClick={() => setShowUpload(true)}>Upload Content</button>
          <button onClick={onRefresh}>Refresh Data</button>
        </div>
      </div>

      {analytics && (
        <div className="analytics-overview">
          <h3>Performance Overview</h3>
          <div className="stats-grid">
            <div className="stat-card">
              <h4>Total Content</h4>
              <p className="stat-number">{analytics.overview?.totalContent || 0}</p>
            </div>
            <div className="stat-card">
              <h4>Total Views</h4>
              <p className="stat-number">{(analytics.overview?.totalViews || 0).toLocaleString()}</p>
            </div>
            <div className="stat-card">
              <h4>Total Revenue</h4>
              <p className="stat-number">${(analytics.overview?.totalRevenue || 0).toLocaleString()}</p>
            </div>
            <div className="stat-card">
              <h4>Avg Views/Content</h4>
              <p className="stat-number">{(analytics.overview?.averageViewsPerContent || 0).toLocaleString()}</p>
            </div>
          </div>
        </div>
      )}

      {showUpload && (
        <ContentUploadForm 
          onUpload={onUpload} 
          onClose={() => setShowUpload(false)}
        />
      )}

      <ContentList content={content} />
    </div>
  );
};

// Content Upload Form Component
const ContentUploadForm = ({ onUpload, onClose }) => {
  const [title, setTitle] = useState('');
  const [type, setType] = useState('video');
  const [url, setUrl] = useState('');
  const [description, setDescription] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    const success = await onUpload({
      title,
      type,
      url,
      description,
      target_platforms: ['youtube', 'tiktok', 'instagram']
    });
    
    if (success) {
      setTitle('');
      setUrl('');
      setDescription('');
      onClose();
    }
    
    setLoading(false);
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="close-btn" onClick={onClose}></button>
        <h2>Upload Content</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Title:</label>
            <input 
              type="text" 
              value={title} 
              onChange={(e) => setTitle(e.target.value)} 
              required 
              disabled={loading}
            />
          </div>
          <div className="form-group">
            <label>Type:</label>
            <select 
              value={type} 
              onChange={(e) => setType(e.target.value)}
              disabled={loading}
            >
              <option value="video">Video</option>
              <option value="audio">Audio</option>
              <option value="image">Image</option>
              <option value="article">Article</option>
            </select>
          </div>
          <div className="form-group">
            <label>URL:</label>
            <input 
              type="url" 
              value={url} 
              onChange={(e) => setUrl(e.target.value)} 
              required 
              disabled={loading}
            />
          </div>
          <div className="form-group">
            <label>Description:</label>
            <textarea 
              value={description} 
              onChange={(e) => setDescription(e.target.value)} 
              rows="3"
              disabled={loading}
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? 'Uploading...' : 'Upload & Promote'}
          </button>
        </form>
      </div>
    </div>
  );
};

// Content List Component
const ContentList = ({ content }) => {
  if (content.length === 0) {
    return (
      <div className="content-section">
        <h3>Your Content</h3>
        <p>No content uploaded yet. Upload your first piece to start promoting!</p>
      </div>
    );
  }

  return (
    <div className="content-section">
      <h3>Your Content ({content.length})</h3>
      <div className="content-grid">
        {content.map((item) => (
          <div key={item.id} className="content-card">
            <h4>{item.title}</h4>
            <p className="content-type">{item.type}</p>
            <p className="content-stats">
               {item.views?.toLocaleString()} views |  ${item.revenue?.toLocaleString()}
            </p>
            <a href={item.url} target="_blank" rel="noopener noreferrer" className="content-link">
              View Content
            </a>
            <div className="content-meta">
              <span>Uploaded: {new Date(item.created_at).toLocaleDateString()}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Admin Dashboard Component
const AdminDashboard = ({ user, onClose, API_BASE }) => {
  const [users, setUsers] = useState([]);
  const [allContent, setAllContent] = useState([]);
  const [platformStats, setPlatformStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadAdminData();
  }, []);

  const loadAdminData = async () => {
    try {
      const token = localStorage.getItem('token');
      const [usersRes, contentRes, statsRes] = await Promise.all([
        fetch(`${API_BASE}/admin/users`, {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch(`${API_BASE}/admin/content`, {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch(`${API_BASE}/admin/overview`, {
          headers: { 'Authorization': `Bearer ${token}` }
        })
      ]);

      if (usersRes.ok) setUsers((await usersRes.json()).users);
      if (contentRes.ok) setAllContent((await contentRes.json()).content);
      if (statsRes.ok) setPlatformStats((await statsRes.json()).platform_stats);
    } catch (error) {
      console.error('Failed to load admin data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">Loading admin data...</div>;

  return (
    <div className="admin-dashboard">
      <div className="dashboard-header">
        <h2>Admin Dashboard</h2>
        <button onClick={onClose}>Back to User View</button>
      </div>

      {platformStats && (
        <div className="admin-stats">
          <h3>Platform Overview</h3>
          <div className="stats-grid">
            <div className="stat-card">
              <h4>Total Users</h4>
              <p className="stat-number">{platformStats.total_users}</p>
            </div>
            <div className="stat-card">
              <h4>Total Content</h4>
              <p className="stat-number">{platformStats.total_content}</p>
            </div>
            <div className="stat-card">
              <h4>Total Views</h4>
              <p className="stat-number">{platformStats.total_views?.toLocaleString()}</p>
            </div>
            <div className="stat-card">
              <h4>Total Revenue</h4>
              <p className="stat-number">${platformStats.total_revenue?.toLocaleString()}</p>
            </div>
          </div>
        </div>
      )}

      <div className="admin-sections">
        <div className="admin-section">
          <h3>Users ({users.length})</h3>
          <div className="users-list">
            {users.map(user => (
              <div key={user.id} className="user-card">
                <h4>{user.name}</h4>
                <p>{user.email}</p>
                <p>Role: {user.role}</p>
                <p>Content: {user.content_count} | Views: {user.total_views?.toLocaleString()}</p>
              </div>
            ))}
          </div>
        </div>

        <div className="admin-section">
          <h3>All Content ({allContent.length})</h3>
          <div className="content-list">
            {allContent.map(item => (
              <div key={item.id} className="content-item">
                <h4>{item.title}</h4>
                <p>By: {item.users?.name} | Type: {item.type}</p>
                <p>Views: {item.views?.toLocaleString()} | Revenue: ${item.revenue?.toLocaleString()}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
