import React, { useState, useCallback } from 'react';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from './firebaseClient';
import './Auth.css';

const LoginForm = ({ onLogin, loginUser }) => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  
  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (error) setError('');
  }, [error]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);
    
    try {
      const { email, password } = formData;
      
      console.log('Attempting login with:', email);
      
      // Sign in with Firebase Authentication
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const { user } = userCredential;

      console.log('Firebase auth successful, getting ID token...');
      
      // Get the ID token
      const idToken = await user.getIdToken(true);  // Force refresh the token to ensure it's up-to-date

      // Verify token and get user data from our backend
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5002';
      const response = await fetch(`${apiUrl}/api/auth/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Origin': 'http://localhost:3000'
        },
        body: JSON.stringify({ idToken })
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Server response:', {
          status: response.status,
          statusText: response.statusText,
          error: errorData
        });
        throw new Error(errorData.error || 'Authentication failed');
      }

      const data = await response.json();
      
      // Pass user info to parent component
      onLogin({
        email: data.user.email,
        uid: data.user.uid,
        role: data.user.role,
        name: data.user.name,
        token: data.token
      });
    } catch (error) {
      console.error('Login error:', error);
      console.error('Error details:', {
        code: error.code,
        message: error.message,
        fullError: JSON.stringify(error, null, 2)
      });
      
      let errorMessage = 'Login failed. ';
      switch (error.code) {
        case 'auth/invalid-credential':
          errorMessage += 'Invalid email or password.';
          break;
        case 'auth/user-not-found':
          errorMessage += 'No account exists with this email.';
          break;
        case 'auth/wrong-password':
          errorMessage += 'Incorrect password.';
          break;
        case 'auth/invalid-api-key':
          errorMessage += 'Invalid Firebase configuration. Please check the setup.';
          break;
        case 'auth/network-request-failed':
          errorMessage += 'Network error. Please check your connection.';
          break;
        default:
          errorMessage += `${error.message} (${error.code})`;
      }
      
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <form onSubmit={handleSubmit} className="auth-form">
        <h2 className="auth-title">Welcome Back</h2>
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}
        
        <div className="form-group">
          <label className="form-label">Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="form-input"
            placeholder="Enter your email"
            required
            autoComplete="email"
          />
        </div>

        <div className="form-group">
          <label className="form-label">Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
                  required
            autoComplete="current-password"
            placeholder="Enter your password"
            className="form-input"
          />
        </div>

        <button 
          type="submit" 
          disabled={isLoading}
          className="auth-button"
        >
          {isLoading ? (
            <>
              <span className="loading-spinner"></span>
              Signing in...
            </>
          ) : (
            'Sign In'
          )}
        </button>

        <a href="#" onClick={() => window.location.reload()} className="auth-link">
          Don't have an account? Create one
        </a>
      </form>
    </div>
  );
};

export default LoginForm;
