import { initializeApp } from 'firebase/app';
import { getAuth, signInWithEmailAndPassword } from 'firebase/auth';
import { getFirestore } from 'firebase/firestore';
import { getStorage } from 'firebase/storage';

// Use environment variables with fallbacks for Firebase configuration
const firebaseConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API_KEY || "AIzaSyASTUuMkz821PoHRopZ8yy1dW5COrAQPZY",
  authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN || "autopromote-464de.firebaseapp.com",
  projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID || "autopromote-464de",
  storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET || "autopromote-464de.firebasestorage.app",
  messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID || "317746682241",
  appId: process.env.REACT_APP_FIREBASE_APP_ID || "1:317746682241:web:f363e099d55ffd1af1b080",
  measurementId: process.env.REACT_APP_FIREBASE_MEASUREMENT_ID || "G-8QDQXF0FPQ"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
export const auth = getAuth(app);
export const db = getFirestore(app);
export const storage = getStorage(app);
