import React, { useState, useEffect } from 'react';
import './App.css';
import { auth, db, storage } from './firebaseClient';
import { onAuthStateChanged } from 'firebase/auth';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import LoginForm from './LoginForm';
import RegisterForm from './RegisterForm';
import ContentUploadForm from './ContentUploadForm';
import ContentList from './ContentList';
import AdminDashboard from './AdminDashboard';
import EnvTest from './components/EnvTest';

function App() {
  const [user, setUser] = useState(() => {
    const stored = localStorage.getItem('user');
    return stored ? JSON.parse(stored) : null;
  });
  const [content, setContent] = useState([]);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [analytics, setAnalytics] = useState(null);

  // Authentication listener
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      if (!firebaseUser) {
        // User is signed out of Firebase
        handleLogout();
        return;
      }

      try {
        // Get a fresh token
        const token = await firebaseUser.getIdToken(true);

        // Check for stored user data
        const storedUser = localStorage.getItem('user');
        const userData = storedUser ? JSON.parse(storedUser) : null;

        if (userData && userData.email === firebaseUser.email) {
          // Update the token in the stored data
          userData.token = token;
          localStorage.setItem('user', JSON.stringify(userData));
          setUser(userData);
          setIsAdmin(userData.role === 'admin');
        }

        console.log('Firebase auth state changed:', firebaseUser.email);
      } catch (error) {
        console.error('Error refreshing token:', error);
        handleLogout();
      }
    });

    // Cleanup subscription
    return () => unsubscribe();
  }, []);

  useEffect(() => {
    if (user && user.token) {
      setIsAdmin(user.role === 'admin');
      if (user.role === 'admin') {
        fetchAnalytics();
      } else {
        fetchUserContent();
      }
    }
    // eslint-disable-next-line
  }, [user]);

  const fetchUserContent = async () => {
    try {
      if (!user || !user.token) {
        console.log('No user or token available');
        return;
      }

      console.log('Fetching content with token');

      const res = await fetch('http://localhost:5000/api/content/my-content', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${user.token}`,
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Origin': 'http://localhost:3000'
        },
        mode: 'cors'
      });

      if (res.status === 401) {
        console.log('Authentication failed, logging out');
        handleLogout();
        return;
      }

      if (!res.ok) {
        console.error('Failed to fetch content:', res.status, await res.text());
        return;
      }

      const data = await res.json();
      setContent(data.content || []);
    } catch (error) {
      console.error('Failed to fetch content:', error);
    }
  };

  const fetchAnalytics = async () => {
    if (!user || !user.token || user.role !== 'admin') {
      console.log('Not authorized to fetch analytics');
      return;
    }

    try {
      console.log('Fetching analytics with token:', user.token);
      const res = await fetch('https://autopromote.onrender.com/api/admin/analytics/overview', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${user.token}`,
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Origin': 'http://localhost:3000'
        },
        mode: 'cors'
      });

      if (res.status === 401) {
        console.log('Admin authentication failed, logging out');
        handleLogout();
        return;
      }

      if (!res.ok) {
        console.error('Failed to fetch analytics:', res.status, await res.text());
        return;
      }

      const data = await res.json();
      setAnalytics(data);
    } catch (error) {
      console.error('Failed to fetch analytics:', error);
    }
  };

  const loginUser = async (email, password) => {
    try {
      // Sign in with Firebase
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const { user: firebaseUser } = userCredential;

      // Get the ID token
      const idToken = await firebaseUser.getIdToken();

      // Verify token with our backend
      const res = await fetch('https://autopromote.onrender.com/api/auth/login', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Origin': 'http://localhost:3000'
        },
        body: JSON.stringify({ idToken }),
      });

      if (res.ok) {
        const data = await res.json();
        handleLogin({
          ...data.user,
          token: data.token
        });
      } else {
        const error = await res.json();
        throw new Error(error.message || 'Login failed');
      }
    } catch (error) {
      console.error('Login error:', error);
      alert(error.message || 'Login failed');
    }
  };

  const registerUser = async (name, email, password) => {
    try {
      const res = await fetch('https://autopromote.onrender.com/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, email, password }),
      });
      if (res.ok) {
        const data = await res.json();
        handleRegister({ ...data.user, token: data.token });
      } else {
        alert('Registration failed');
      }
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration error');
    }
  };

  const handleLogin = async (userData) => {
    try {
      // Clear any existing data
      localStorage.clear();

      // Ensure we have a token
      if (!userData.token) {
        console.error('No token provided in user data');
        return;
      }

      // Log the authentication data
      console.log('Logging in user:', {
        email: userData.email,
        role: userData.role,
        hasToken: Boolean(userData.token)
      });

      // Store the complete user data
      localStorage.setItem('user', JSON.stringify(userData));

      // Update the state
      setUser(userData);
      setIsAdmin(userData.role === 'admin');
      setShowLogin(false);

      console.log('Login successful:', userData.role);

      // Fetch data based on user role
      if (userData.role === 'admin') {
        await fetchAnalytics();
      } else {
        await fetchUserContent();
      }
    } catch (error) {
      console.error('Error during login:', error);
      handleLogout();
    }
  };

  const handleRegister = (userData) => {
    setUser(userData);
    setShowRegister(false);
  };

  const handleLogout = async () => {
    try {
      // Sign out from Firebase
      await auth.signOut();

      // Clear local state
      setUser(null);
      setContent([]);
      setAnalytics(null);
      setIsAdmin(false);

      // Clear local storage
      localStorage.removeItem('user');
      localStorage.removeItem('token');
      localStorage.removeItem('adminToken');
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  // Firebase Storage upload
  const handleUploadContent = async (contentData) => {
    try {
      let url = '';
      if (contentData.type === 'article') {
        url = contentData.articleText || '';
      } else {
        // Upload file to Firebase Storage
        const file = contentData.file;
        if (!file) {
          alert('No file selected!');
          return;
        }

        // Debug: log file info
        console.log('Uploading file:', file);

        // Check file type and size (optional)
        if (file.size > 50 * 1024 * 1024) {
          alert('File is too large. Max 50MB allowed.');
          return;
        }

        const filePath = `content/${Date.now()}_${file.name}`;
        const storageRef = ref(storage, filePath);

        // Upload the file
        await uploadBytes(storageRef, file);

        // Get the download URL
        url = await getDownloadURL(storageRef);

        if (!url) {
          alert('Could not get download URL for uploaded file.');
          return;
        }
      }

      const payload = {
        title: contentData.title,
        type: contentData.type,
        url,
        description: contentData.description || '',
      };

      const res = await fetch('https://autopromote.onrender.com/api/content/upload', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify(payload),
      });

      if (res.ok) {
        fetchUserContent();
      } else {
        const error = await res.json();
        console.error('Failed to upload content', error);
        alert(error.error || 'Failed to upload content');
      }
    } catch (error) {
      console.error('Error uploading content:', error);
      alert('Error uploading content: ' + error.message);
    }
  };

  return (
    <div className="App">
      <EnvTest />
      <header className="App-header">
        <h1>AutoPromote</h1>
        <nav>
          {user ? (
            <div>
              <span>Welcome, {user.name}!</span>
              <button onClick={handleLogout}>Logout</button>
            </div>
          ) : (
            <div>
              <button onClick={() => { setShowLogin(true); setShowRegister(false); }}>Login</button>
              <button onClick={() => { setShowRegister(true); setShowLogin(false); }}>Register</button>
            </div>
          )}
        </nav>
      </header>

      <main>
        {showLogin && <LoginForm onLogin={handleLogin} loginUser={loginUser} />}
        {showRegister && <RegisterForm registerUser={registerUser} />}

        {user && (
          <>
            {isAdmin ? (
              <AdminDashboard analytics={analytics} user={user} />
            ) : (
              <div>
                <ContentUploadForm onUpload={handleUploadContent} />
                <ContentList content={content} />
              </div>
            )}
          </>
        )}

        {!user && !showLogin && !showRegister && (
          <div className="WelcomeSection" style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            minHeight: '60vh',
            background: 'linear-gradient(135deg, #1976d2 0%, #64b5f6 100%)',
            color: '#fff',
            borderRadius: '16px',
            boxShadow: '0 8px 32px rgba(25, 118, 210, 0.2)',
            padding: '48px 24px',
            margin: '32px auto',
            maxWidth: '500px',
          }}>
            <img src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" alt="AutoPromote Logo" style={{ width: 80, marginBottom: 24 }} />
            <h2 style={{ fontSize: '2.5rem', fontWeight: 700, marginBottom: 16 }}>Welcome to AutoPromote</h2>
            <p style={{ fontSize: '1.2rem', marginBottom: 32, textAlign: 'center', maxWidth: 400 }}>
              <span style={{ fontWeight: 500 }}>AI-powered platform</span> for content promotion and monetization.<br />
              Grow your audience, boost your revenue, and automate your success.
            </p>
            <button 
              onClick={() => setShowRegister(true)}
              style={{
                background: '#fff',
                color: '#1976d2',
                fontWeight: 600,
                fontSize: '1.1rem',
                padding: '12px 32px',
                borderRadius: '8px',
                border: 'none',
                boxShadow: '0 2px 8px rgba(25, 118, 210, 0.15)',
                cursor: 'pointer',
                transition: 'background 0.2s',
              }}
              onMouseOver={e => e.target.style.background = '#e3f2fd'}
              onMouseOut={e => e.target.style.background = '#fff'}
            >
              Get Started
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

